from hexes import *
from PIL import Image, ImageDraw, ImageFont
import unittest

class TestHexImagePlacement(unittest.TestCase):

    """Simply run and test the initializer."""

    def runTest(self):
        """Make sure the attributes are set properly."""
        p = HexImagePlacement((100, 200, 300, 400), 45,
                              (10, 20, 30, 40), [(0,0), (0,1)])
        self.assertEqual(p.image_rect, (100, 200, 300, 400))
        self.assertEqual(p.rotation, 45)
        self.assertEqual(p.letter_rect, (10, 20, 30, 40))
        self.assertEqual(p.offsets_used, [(0,0), (0,1)])


class TestHexDrawClassAttributes(unittest.TestCase):

    """Test the various public class attributes of HexDraw."""

    def test_directions(self):
        """Make sure class attributes are accessible and correct."""
        self.assertEqual(HexDraw.NORTH, 0)
        self.assertEqual(HexDraw.NORTHEAST, 1)
        self.assertEqual(HexDraw.SOUTHEAST, 2)
        self.assertEqual(HexDraw.SOUTH, 3)
        self.assertEqual(HexDraw.SOUTHWEST, 4)
        self.assertEqual(HexDraw.NORTHWEST, 5)
        self.assertEqual(HexDraw.EAST, 6)
        self.assertEqual(HexDraw.WEST, 7)

    def test_standard_colors(self):
        """Make sure class attributes are accessible and correct."""
        self.assertEqual(HexDraw.BACK_COLOR, (255, 100, 100, 255))
        self.assertEqual(HexDraw.FRONT_COLOR, (100, 255, 100, 255))
        self.assertEqual(HexDraw.SIDE_COLOR, (255, 255, 100, 255))
        self.assertEqual(HexDraw.BG_COLOR, (255, 255, 255, 255))
        self.assertEqual(HexDraw.OUTLINE_COLOR, (0, 0, 0, 255))

    def test_shapes(self):
        """Make sure class attributes are accessible and correct."""
        self.assertEqual(HexDraw.SHAPE_LONG, 0)
        self.assertEqual(HexDraw.SHAPE_ROUND, 1)
        self.assertEqual(HexDraw.SHAPE_WAVY, 2)
        self.assertEqual(HexDraw.SHAPE_CURLY, 3)


class TestHexDrawBasics(unittest.TestCase):

    """Test public methods of HexDraw."""

    def setUp(self):
        """Create a usable HexDraw instance."""
        self.image = Image.new("RGBA", (100, 100))
        self.hexdraw = HexDraw(ImageDraw.Draw(self.image), (0, 0, 100, 100))
        
    def test_geometry(self):
        """Confirm mathematical geometry."""
        self.assertEqual(self.hexdraw.rect, (0, 0, 100, 100))
        self.assertEqual(self.hexdraw.NW, (25, 0))
        self.assertEqual(self.hexdraw.NE, (75, 0))
        self.assertEqual(self.hexdraw.W, (0, 50))
        self.assertEqual(self.hexdraw.E, (100, 50))
        self.assertEqual(self.hexdraw.SW, (25, 100))
        self.assertEqual(self.hexdraw.SE, (75, 100))
        self.assertEqual(self.hexdraw.outer_corners,
                         ((25, 0), (75, 0), (100, 50), (75, 100),
                          (25, 100), (0, 50), (25, 0)))
        self.assertEqual(self.hexdraw.inner_corners,
                         ((28, 6), (72, 6), (94, 50), (72, 94),
                          (28, 94), (6, 50), (28, 6)))

