    def test_simple_add(self):
        """Just add one-by-one until we run out of room."""
        p = HexPage(pagesize=(2,2))
        self.assertEqual(p.add(1), (0, 0))
        self.assertEqual(p.add(1), (1, 0))
        self.assertEqual(p.add(1), (0, 1))
        self.assertEqual(p.add(1), (1, 1))
        self.assertRaises(HexPageOverflowError, p.add, 1)

    def test_add_doubles(self):
        """No rotation yet, so it overflows quickly."""
        p = HexPage(pagesize=(5, 1))
        self.assertRaises(HexPageOverflowError, p.add, 2)

    def test_work_around(self):
        """Multi-hex areas should fit in around each other."""
        p = HexPage(pagesize=(3, 4))
        self.assertEqual(p.add(1), (0, 0))
        self.assertEqual(p.add(3, HexDraw.SHAPE_ROUND), (1, 0))
        self.assertEqual(p.add(1), (0, 1))
        self.assertEqual(p.add(1), (2, 1))
        self.assertEqual(p.add(1), (0, 2))

    def test_add_with_existing(self):
        """Multi-hex areas should fit into existing framework."""
        p = HexPage(pagesize=(3, 4))
        p.place((1,0), 2)
        p.place((0,2), 1)
        self.assertEqual(p.add(3, HexDraw.SHAPE_ROUND), (1, 2))
        self.assertEqual(p.add(1), (0, 0)) # single insertions NOT affected

